import React from 'react'
import Filho from './filho'

export default props => 
    <div>
        <h1>{props.nome} {props.sobrenome}</h1>
        <h2>Filhos</h2>
        <ul>
            
            {
                React.Children.map(props.children, child => {
                    React.cloneElement(props.children, {
                        ...props, ...props.children.props
                    })
                })
            }
        </ul>
    </div>
